  width: 100%;
  background: #fff;
  border-radius: 15px;
  box-shadow: 0 2px 20px rgba(0, 0, 0, 0.05);
  padding: 30px;
  font-family: "Segoe UI", Tahoma, Geneva, Verdana, sans-serif;

  .upload-box {
    border: 1.5px dashed #c3c6d4;
    padding: 40px 20px;
    text-align: center;
    border-radius: 8px;
    background-color: #ffffff;
    // position: relative;
    display: flex;
    align-items: center;
    justify-content: center;
    flex-direction: column;
    transition: border-color 0.3s;
