      margin-top: 13px;
      margin-bottom: 9px;
      font-size: 12px;
      font-weight: 500;
      color: #676879;
      display: flex;
      align-items: center;
      justify-content: space-between;
    }
    .progress-bar {
      height: 10px;
      border-radius: 8px;
      background: #e0e0e0;
      overflow: hidden;

      .progress-fill {
        height: 100%;
        width: 50%; // Update dynamically via style or class
        background: linear-gradient(to right, #00f0ff, #ff00e5);
        border-radius: 8px;
        transition: width 0.4s ease-in-out;
      }
    }
  }
